import time
import random
print("Enter your username")
name = input()
def start():
  number = random.randint(0,)
  if number == 1:
    print("You did it! Your number is 1! Adding you to database!")
    database = open("highscores.txt","a")
    database.write(name + "\n")
    database.close()
  else:
    print("Nope your number was",number)
while(True):
  start()
  print("Do you want to start again? (y,n)")
  y = input()
  if(y == "y"):
    start()
  else:
    print("Ok! Bye!")
    break